import React from "react";

import { Form, FormInput, FormGroup, Button } from "shards-react";
import axios from "axios"
import API from "./utils/API"

class CardForm extends React.Component {
  handleChange = event => {
    console.log('-'.repeat(50), 'event', event);
    this.setState({ name: event.target.value });
  }
  handleSubmit = event => {
    event.preventDefault();

    const card = {
      name: this.state.name,
      number: this.state.number,
      balance: this.state.balance,
    };

    axios.post(`http://localhost:3005/v1/cards`, { card })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }
  render() {
    return (
      <Form onSubmit={this.handleSubmit} className="mx-auto mt-4 text-center p-1" style={{ maxWidth: "300px", minHeight: "250px" }}>
        <FormGroup>
          <label htmlFor="#cardName">Name</label>
          <FormInput id="#cardName" name="name" placeholder="Name on card" />
        </FormGroup>
        <FormGroup>
          <label htmlFor="#cardNumber">Card Number</label>
          <FormInput id="#cardNumber" name="number" placeholder="Card number" />
        </FormGroup>
        <FormGroup>
          <label htmlFor="#balance">Balance</label>
          <FormInput id="#balance" name="balance" placeholder="Initial balance" />
        </FormGroup>
        <Button outline>Add</Button>
      </Form>
    )
  }
}

export default CardForm;
