import React from "react";
import PropTypes from "prop-types";

import "bootstrap/dist/css/bootstrap.min.css";
import "shards-ui/dist/css/shards.min.css";

import { Card, Form, FormInput, FormGroup, Button, Badge, Container, Row, Col } from "shards-react";

class CardScreen extends React.Component {
  render() {
    const { name, number, balance, isLoading } = this.props;

    const cardDetails = (
      <div>
        {/* <img
          className="img-thumbnail rounded-circle mx-auto mb-2 shadow-sm"
          src={avatar}
          alt={name}
          style={{ width: "100px", height: "100px" }}
        /> */}
        <h4 className="mb-0">{name}</h4>
        <Badge theme="success"> Number: {number}</Badge> <br />
        <Badge theme="info">Â£{balance}</Badge>
      </div>
    );

    const loadingMessage = <span className="d-flex m-auto">Loading...</span>;

    return (
      <>
        <Container className="dr-example-container">
          <Form className="mx-auto mt-4 text-center p-1" style={{ maxWidth: "300px", minHeight: "250px" }}>
            <FormGroup>
              <label htmlFor="#cardName">Name</label>
              <FormInput id="#cardName" placeholder="Name on card" />
            </FormGroup>
            <FormGroup>
              <label htmlFor="#cardNumber">Card Number</label>
              <FormInput id="#cardNumber" placeholder="Card number" />
            </FormGroup>
            <FormGroup>
              <label htmlFor="#balance">Balance</label>
              <FormInput id="#balance" placeholder="Initial balance" />
            </FormGroup>
            <Button outline>Add</Button>
          </Form>
        </Container>
        <Card
          className="mx-auto mt-4 text-center p-5"
          style={{ maxWidth: "300px", minHeight: "150px" }}
        >
          {isLoading ? loadingMessage : cardDetails}
        </Card>
      </>
    );
  }
}

CardScreen.propTypes = {
  name: PropTypes.string,
  number: PropTypes.number,
  balance: PropTypes.number,
  isLoading: PropTypes.bool
};

export default CardScreen;
