import React from "react";

import { Form, FormInput, FormGroup, Button } from "shards-react";
import API from "./utils/API"

class CardForm extends React.Component {
  handleSubmit = event => {
    event.preventDefault();

    const user = {
      name: this.state.name,
      number: this.state.number,
      balance: this.state.balance,
    };

    axios.post(`http://localhost:3005/v1/cards`, { user })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }
  render() {
    return (
      <Form className="mx-auto mt-4 text-center p-1" style={{ maxWidth: "300px", minHeight: "250px" }}>
        <FormGroup>
          <label htmlFor="#cardName">Name</label>
          <FormInput id="#cardName" placeholder="Name on card" />
        </FormGroup>
        <FormGroup>
          <label htmlFor="#cardNumber">Card Number</label>
          <FormInput id="#cardNumber" placeholder="Card number" />
        </FormGroup>
        <FormGroup>
          <label htmlFor="#balance">Balance</label>
          <FormInput id="#balance" placeholder="Initial balance" />
        </FormGroup>
        <Button outline>Add</Button>
      </Form>
    )
  }
}

export default CardForm;
